def nearest(train_data,test_data,test_names):
	count=0
	final_distances=[]
	#find euclidean distance of each test image with every train image
	for test in test_data:
		print("Testing Image :",count+1)
		count+=1
		distances=[]
		for train in train_data:
			dist1 = np.linalg.norm(test[1:]-train[1:])#euclidean distance
			distances.append(dist1)
		final_distances.append(distances)#list of no. of test images with each index in the list consisting of all the distances of each test image with every train image
	print("Calculating Accuracy.......")
	#define k value
	k=11
	final_min_values=[]
	final_index_values=[]
	new_distances=copy.deepcopy(final_distances)
	for list1 in new_distances:
	    min_list =[]
	    min_index=[]

	    cpList = copy.copy(list1)  # we want to copy the list so that we can reference the index later on  
	                               # a shallow copy will also work, less expensive than a deepcopy  
	    #Loop for finding k minimum distances of each image
	    for i in range(0, k):     
	        min1 = 9999999;
	        for j in range(len(cpList)):     
	            if cpList[j]<min1:          
	                min1 = cpList[j]        
	        min_list.append(min1)

	        ind = list1.index(min1)  
	        min_index.append(ind)    

	        cpList.remove(min1)
	    final_min_values.append(min_list)
	    final_index_values.append(min_index)

	final_orientation_list=[]
	orientation_list=[]
	for k in final_index_values:
		orientation_list=[]
		for i in k:
			orientation_list.append(train_data[i][0])
		final_orientation_list.append(orientation_list)
	new_ori=[]
	for i in final_orientation_list:
		results = list(map(int, i))
		new_ori.append(results)
	final_ori=[]
	for i in new_ori:
		x=max(i,key=i.count)
		final_ori.append(x)

	results_list=[str(x) for x in final_ori]
	accuracy=0.0
	output_list=[]
	#compare our orientation with test labels to find accuracy
	for i in range(0,len(test_names)):
		temp_op=[]
		a=results_list[i]
		b=test_names[i][0]
		temp_op.append(b)
		temp_op.append(a)
		output_list.append(temp_op)
		if test_names[i][1]==results_list[i]:
			accuracy+=1
	accuracy=(float(accuracy)/len(test_names))*100 
	print("Accuracy = ",accuracy,"%")
	with open('output_nearest.txt', 'w') as f:
		for _list in output_list:
			f.write("\n")
			for _string in _list:
				f.write(str(_string))
				f.write(" ")
	f.close()

